mapper.insert:
insert into technology.policy_related (pr_title, pr_level,
  pr_time, pr_file, pr_content
  )
values (#{prTitle,jdbcType=VARCHAR}, #{prLevel,jdbcType=INTEGER},
  #{prTime,jdbcType=DATE}, #{prFile,jdbcType=VARCHAR}, #{prContent,jdbcType=LONGVARCHAR}
  )

mapper.select:
select pr_id, pr_title, pr_level, pr_time, pr_file, pr_content
from technology.policy_related
where pr_id = #{prId,jdbcType=INTEGER}

select * from technology.policy_related
and policy_related.pr_level = #{level,jdbcType=INTEGER}
and policy_related.pr_title like concat('%', #{title,jdbcType=VARCHAR}, '%')
